import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons'
import './Upload.scss'
// import './buttons.js'

import axios from 'axios'

import { useState } from 'react';

function Carousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  function updateCarousel() {
    const carouselItems = document.querySelectorAll('.carousel_item');
    const cardIndicators = document.querySelectorAll('.card_indicator');

    carouselItems.forEach((item, index) => {
      if (index === currentIndex) {
        item.classList.add('active');
      } else {
        item.classList.remove('active');
      }
    });

    cardIndicators.forEach((indicator, index) => {
      if (index === currentIndex) {
        indicator.classList.add('active');
      } else {
        indicator.classList.remove('active');
      }
    });
  }

  function handlePrevClick() {
    const carouselItems = document.querySelectorAll('.carousel_item');
    setCurrentIndex((currentIndex - 1 + carouselItems.length) % carouselItems.length);
  }

  function handleNextClick() {
    const carouselItems = document.querySelectorAll('.carousel_item');
    setCurrentIndex((currentIndex + 1) % carouselItems.length);
  }

  function handleIndicatorClick(index) {
    setCurrentIndex(index);
  }

  return (
    <div class="file-card1">
            <div class="carousel">
              <div class="carousel_item active">
                <img src="https://www.referenseo.com/wp-content/uploads/2019/03/image-attractive.jpg" />
              </div>
              <div class="carousel_item">
                <img src="https://hatrabbits.com/wp-content/uploads/2017/01/random.jpg" />
              </div>
              <div class="carousel_item">
                <img src="https://images.unsplash.com/photo-1494253109108-2e30c049369b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTB8fHJhbmRvbXxlbnwwfHwwfHw%3D&w=1000&q=80" />
              </div>
            </div>
            <div class="carousel_controls">
              {/* <button class="prev_button">&lt;</button>
              <div class="card_indicators">
                <div class="card_indicator active"></div>
                <div class="card_indicator"></div>
                <div class="card_indicator"></div>
              </div>
              <button class="next_button">&gt;</button> */}


            </div>
          </div>

  );
}

// export default Carousel;

const upload = ({ files, setFiles, removeFile }) => {
    const uploadHandler = (event) => {
        const file = event.target.files[0];
        if(!file) return;
        file.isUploading = true;
        setFiles([...files, file])

        // upload file
        const formData = new FormData();
        formData.append(
            "newFile",
            file,
            file.name
        )
        axios.post('http://localhost:8080/upload', formData)
            .then((res) => {
                file.isUploading = false;
                setFiles([...files, file])
            })
            .catch((err) => {
                // inform the user
                console.error(err)
                removeFile(file.name)
            });
    }



    return (
      <>
          <div className="file-card">
            <div className="file-inputs">
              <input type="file" accept="audio/mpeg3" onChange={uploadHandler} />
              <button>
                <i>
                  <FontAwesomeIcon icon={faPlus} />
                </i>
                Upload
              </button>
            </div>
            <p className="main">Supported files</p>
            <p className="info">M4A, MP3, WAV, FLAC</p>
          </div>
