import React,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons'
import './Upload.scss'
// import './buttons.js'
import axios from 'axios'
import ReactDOM from 'react-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css"; 
import { Carousel } from 'react-responsive-carousel';



const uploadHandler = (event) => {
  const file = event.target.files[0];
  if (!file) return;
  file.isUploading = true;
  setFiles([...files, file]);

  // upload file
  const formData = new FormData();
  formData.append('file', file);
  axios.post('http://43.204.145.99:8081/s3/upload', formData)
    .then((response) => {
      file.isUploading = false;
      setFiles([...files, file]);
      console.log('File uploaded successfully:', response.data);
    })
    .catch((error) => {
      // inform the user
      console.error('Error uploading file:', error);
      removeFile(file.name);
    });



    return (
      <>
          <div className="file-card">
            <div className="file-inputs">
              <input type="file" accept="audio/mpeg3" onChange={uploadHandler} />
              <button>
                <i>
                  <FontAwesomeIcon icon={faPlus} />
                </i>
                Upload
              </button>
            </div>
            <p className="main">Supported files</p>
            <p className="info">M4A, MP3, WAV, FLAC</p>
          </div>

          <Carousel className="file-card1" showThumbs={false} showStatus={false} useKeyboardArrows={true} showArrows={true} showIndicators={true}

            renderIndicator={(onClickHandler, isSelected, index, label) => {
              const defStyle = { marginLeft: 20, color: "white", cursor: "pointer" };
              const style = isSelected
                ? { ...defStyle, color: " " }
                : { ...defStyle };
              return (
                <span
                  style={style}
                  onClick={onClickHandler}
                  onKeyDown={onClickHandler}
                  value={index}
                  key={index}
                  role="button"
                  tabIndex={0}
                  aria-label={`${label} ${index + 1}`}
                >
                  {"●"}
                </span>
              );
            }}

          >
                <div className="carousel">
                {/* <img src="https://www.referenseo.com/wp-content/uploads/2019/03/image-attractive.jpg" /> */}
                <a href="https://www.google.com" target="_blank" rel="noopener noreferrer">
                  <button type="button" style={{ cursor: 'pointer' , fontWeight: 'bold', background: ''}}>Audio Fingerprinting</button>
                </a>  
                </div>
                <div className="carousel">
                {/* <img src="https://hatrabbits.com/wp-content/uploads/2017/01/random.jpg" /> */}
                <a href="https://www.google.com" target="_blank" rel="noopener noreferrer">
                  <button type="button" style={{ cursor: 'pointer', fontWeight: 'bold', background: '#FBEAFF' }}>Genre Classification</button>
                </a> 
                </div>
                <div className="carousel">
                {/* <img src="https://images.unsplash.com/photo-1494253109108-2e30c049369b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTB8fHJhbmRvbXxlbnwwfHwwfHw%3D&w=1000&q=80" /> */}
                <a href="https://www.google.com" target="_blank" rel="noopener noreferrer">
                  <button type="button" style={{ cursor: 'pointer', fontWeight: 'bold', background: '#FBEAFF' }}>Instrument Detection</button>
                </a> 
                </div>

            </Carousel>
      </>
    );
}



export default upload;
